namespace Bugo\LightPortal\Addons\Giscus;

use Bugo\LightPortal\Addons\Plugin;

if (! defined('LP_NAME'))
	die('No direct access...');

/**
 * Generated by PluginMaker
 */
class Giscus extends Plugin
{
	public string $type = 'comment';

	private string $url = 'https://giscus.app';

	private array $themes = [
		'light'               => 'GitHub Light',
		'light_high_contrast' => 'GitHub Light High Contrast',
		'light_protanopia'    => 'GitHub Light Protanopia & Deuteranopia',
		'light_tritanopia'    => 'GitHub Light Tritanopia',
		'dark'                => 'GitHub Dark',
		'dark_high_contrast'  => 'GitHub Dark High Contrast',
		'dark_protanopia'     => 'GitHub Dark Protanopia & Deuteranopia',
		'dark_tritanopia'     => 'GitHub Dark Tritanopia',
		'dark_dimmed'         => 'GitHub Dark Dimmed',
		'transparent_dark'    => 'Transparent Dark',
		'cobalt'              => 'RStudio Cobalt',
	];

	public function init()
	{
		$this->txt['lp_show_comment_block_set']['giscus'] = 'Giscus';
	}

	public function addSettings(array &$config_vars)
	{
		$this->addDefaultValues([
			'theme' => 'light'
		]);

		$config_vars['giscus'][] = ['text', 'repo', 'subtext' => sprintf($this->txt['lp_giscus']['repo_subtext'], $this->url)];
		$config_vars['giscus'][] = ['text', 'repo_id', 'subtext' => sprintf($this->txt['lp_giscus']['repo_id_subtext'], $this->url)];
		$config_vars['giscus'][] = ['text', 'category', 'subtext' => sprintf($this->txt['lp_giscus']['category_subtext'], $this->url)];
		$config_vars['giscus'][] = ['text', 'category_id', 'subtext' => sprintf($this->txt['lp_giscus']['category_id_subtext'], $this->url)];
		$config_vars['giscus'][] = ['select', 'theme', $this->themes];
	}

	public function comments()
	{
		if (! empty($this->modSettings['lp_show_comment_block']) && $this->modSettings['lp_show_comment_block'] === 'giscus' && ! empty($this->context['lp_giscus_plugin']['repo'])) {
			$this->context['lp_giscus_comment_block'] = '
			<div class="giscus windowbg"></div>
			<script src="https://giscus.app/client.js"
				data-repo="' . $this->context['lp_giscus_plugin']['repo'] . '"
				data-repo-id="' . $this->context['lp_giscusplugin']['repo_id'] . '"
				data-category="' . $this->context['lp_giscus_plugin']['category'] . '"
				data-category-id="' . $this->context['lp_giscus_plugin']['category_id'] . '"
				data-mapping="title"
				data-strict="1"
				data-reactions-enabled="1"
				data-emit-metadata="0"
				data-input-position="bottom"
				data-theme="' . $this->context['lp_giscus_plugin']['theme'] . '"
				data-lang="' . (in_array($this->txt['lang_dictionary'], ['ar', 'de', 'es', 'fa', 'fr', 'id', 'it', 'ja', 'ko', 'nl', 'pl', 'pt', 'ro', 'ru', 'th', 'tr', 'vi', 'zh-CN', 'zh-TW']) ? $this->txt['lang_dictionary'] : 'en') . '"
				data-loading="lazy"
				crossorigin="anonymous"
				async>
			</script>';
		}
	}
}
